import React, { useContext, useState } from "react";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import TextField from "@mui/material/TextField";
import { Navigate, Link } from "react-router-dom";
import Button from "@mui/material/Button";
import { AuthContext } from "../../contexts/authContext"


const TemplateLoginPage = props => {
  const context = useContext(AuthContext)
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const login = () => {
    console.log("yippie")
    context.authenticate(username, password);
  };

  if (context.isAuthenticated === true) {
    return <Navigate to={"/"} />;
  }

  return (
    <>
      {/* <h2>Login</h2>
      <input
        id="username"
        placeholder="username"
        onChange={e => {
          setUsername(e.target.value);
        }}></input><br />
        
        <input
        id="password"
        type="password"
        placeholder="password"
        onChange={e => {
          setPassword(e.target.value);
        }}></input><br />
        <button onClick={login}>Log in</button> */}
      <Box component ="div" pt={10} sx={{ justifyContent:'center', display: 'flex' }}>
          <Typography component="h2" variant="h3">
            Log into TMDB
          </Typography>
        </Box>
        <Box component ="div" pt={5} sx={{ justifyContent:'center', display: 'flex'}}>
          <TextField
           id="username"
            label="Username"
           type="text"
           autoFocus
           onChange={e => {setUsername(e.target.value)}}
          />
        </Box>
        <Box component ="div" pt={1} sx={{ justifyContent:'center',display: 'flex'}}>
          <TextField
            id="password"
            label="Password"
            type="password"
            autoComplete="current-password"
            onChange={e => {setPassword(e.target.value)}}
          />
        </Box>
        <Box component ="div" pt={1} sx={{ justifyContent:'center',display: 'flex', color:'orange'}}>
          <Button
          variant="contained"
          color="primary"
          sx = {{ backgroundColor: "orange" }}
          onClick={login}
          >
            Log in
          </Button>
        </Box> 
        <Box component ="div" pt={1} sx={{ justifyContent:'center',display: 'flex'}}>
          <Button
          variant="contained"
          color="primary"
          sx = {{ backgroundColor: "orange" }}
          onClick={login}
          >
            Sign Up
          </Button>
        </Box> 
    </>
  );
};

export default TemplateLoginPage;
